  title:'Atomic habit',
  author:'James Clear',
}
const secondBook ={
  image:'https://m.media-amazon.com/images/I/41656QTKoZL._SY445_SX342_.jpg',
  title:'King of Sloth',
  author:'Ana Huang',
}

