        <Book image={image} title={title} author={author}></Book>
        
      </section>
    )

};

const Book = (props) => {
  return (
      <article className='book'>
          <img src={props.image} alt={props.title}></img>
          <h2>{props.title}</h2>
          <h4>{props.author}</h4>
      </article>
  );
};





const root = ReactDOM.createRoot(document.getElementById('root'));


root.render(<BookList/>);
